import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";

class ThirdGallery extends Component {
  state = {
    movies: [],
  };

  componentDidMount() {
    fetch("http://www.omdbapi.com/?apikey=8034813b&s=star%20wars&type=movie")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Errore nella fetch");
        }
        return response.json();
      })
      .then((movieData) => {
        const sixMovies = movieData.Search.slice(0, 6);
        this.setState({ movies: sixMovies });
      })
      .catch((error) => {
        console.error("Errore durante il recupero dei dati del film:", error);
      });
  }

  render() {
    return (
      <>
        <Row className="gap-5">
          <h3 className="mb-4">New Releases</h3>
          {this.state.movies.map((movie) => (
            <Col key={movie.imdbID} className="col-6 col-md-4 col-lg mb-4">
              <div className="border-0">
                <img
                  src={movie.Poster}
                  className="w-max-100"
                  alt={movie.Title}
                  draggable="false"
                />
              </div>
            </Col>
          ))}
        </Row>
      </>
    );
  }
}

export default ThirdGallery;
