import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";

class FirstGallery extends Component {
  state = {
    movies: [],
    error: null,
  };

  componentDidMount() {
    fetch(
      "http://www.omdbapi.com/?apikey=8034813b&s=harry%20potter&type=movie&r=json&plot=full&page=1&limit=6"
    )
      .then((response) => {
        if (!response.ok) {
          throw new Error("Errore nella fetch");
        }
        return response.json();
      })
      .then((movieData) => {
        this.setState({ movies: [movieData] });
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.error("Errore durante il recupero dei dati del film:", error);
      });
  }

  render() {
    return (
      <>
        <h3 className="mb-4">Trending Now</h3>
        <Row>
          {this.state.movies.map((movie) => (
            <Col key={movie.imdbID} className="col-6 col-md-4 col-lg mb-4">
              <div className="border-0">
                {movie && (
                  <img
                    src={movie.Poster}
                    className="w-max-100"
                    alt={movie.Title}
                    draggable="false"
                  />
                )}
              </div>
            </Col>
          ))}
        </Row>
      </>
    );
  }
}

export default FirstGallery;
