import React, { Component } from "react";
import { Alert, Col, Row } from "react-bootstrap";

class MyGallery extends Component {
  state = {
    movies: [],
    nameMovies: this.props.nameMovies,
    isError: false,
    isLoading: true, // Aggiunta dello stato per gestire il caricamento
  };

  showGallery = (nameMovies) => {
    fetch(`http://www.omdbapi.com/?apikey=803481b&s=${nameMovies}&type=movie`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Errore nella fetch");
        }
        return response.json();
      })
      .then((movieData) => {
        const sixMovies = movieData.Search.slice(0, 6);
        this.setState({ movies: sixMovies, isError: false, isLoading: false });
      })
      .catch((error) => {
        console.error("Errore:", error);
        this.setState({ isError: true, isLoading: false });
      });
  };

  componentDidMount() {
    this.showGallery(this.state.nameMovies);
  }

  render() {
    return (
      <>
        {this.state.isLoading && <p>Caricamento...</p>}
        {this.state.isError && (
          <Alert variant="danger">Si Ã¨ verificato un errore</Alert>
        )}
        <Row className="gap-2">
          <h3 className="mb-4">
            The Best Of
            <strong>
              {" " +
                this.state.nameMovies.charAt(0).toUpperCase() +
                this.state.nameMovies.slice(1)}
            </strong>
          </h3>
          {this.state.movies.map((movie) => (
            <Col key={movie.imdbID} className="col-6 col-md-4 col-lg mb-4">
              <div className="border-0">
                <img
                  src={movie.Poster}
                  className="w-max-100"
                  alt={movie.Title}
                  draggable="false"
                />
              </div>
            </Col>
          ))}
        </Row>
      </>
    );
  }
}

export default MyGallery;
