import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";

class MyGallery extends Component {
  state = {
    movies: [],
    nameMovies: this.props.nameMovies,
  };

  showGallery = (nameOfMovie) => {
    fetch(`http://www.omdbapi.com/?apikey=8034813b&s=${nameOfMovie}&type=movie`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Errore nella fetch");
        }
        return response.json();
      })
      .then((movieData) => {
        const sixMovies = movieData.Search.slice(0, 6);
        this.setState({ movies: sixMovies });
      })
      .catch((error) => {
        console.error("Errore durante il recupero dei dati del film:", error);
      });
  };
  componentDidMount() {
    this.showGallery(this.state.nameMovies);
  }

  render() {
    return (
      <>
        <Row className="gap-2">
          <h3 className="mb-4">Il meglio di {this.state.nameMovies}</h3>
          {this.state.movies.map((movie) => (
            <Col key={movie.imdbID} className="col-6 col-md-4 col-lg mb-4">
              <div className="border-0">
                <img
                  src={movie.Poster}
                  className="w-max-100"
                  alt={movie.Title}
                  draggable="false"
                />
              </div>
            </Col>
          ))}
        </Row>
      </>
    );
  }
}

export default MyGallery;
